		<a href=javascript:openExamples("qR(E-E)")>Example queries.</a>	 -->  </table></form><?php } ?></table>  <p><?php$rel = $HTTP_GET_VARS['rel'];$e = $HTTP_GET_VARS['e'];$t = $HTTP_GET_VARS['t'];$e1 = $HTTP_GET_VARS['e1'];$e2 = $HTTP_GET_VARS['e2'];if ($submit == 'qT-E' and $e){	$searched = 1;	$fields1 = "eid2,str2,tid1,str1,reltype,eid2,str2,tmlfile,sentid1,sentid2";	$fields2 = "eid1,str1,eid1,str1,reltype,tid2,str2,tmlfile,sentid1,sentid2";	$q1 = "SELECT $fields1 from $EXPANDED_TLINKS_TABLE WHERE str2='$e' AND eid1=''";	$q2 = "SELECT $fields2 from $EXPANDED_TLINKS_TABLE WHERE str1='$e' AND eid2=''";	$rows = queryDB($q1,$q2);}elseif ($submit == 'qE-T' and $t){	$searched = 1;	$fields1 = "tid2,str2,eid1,str1,reltype,tid2,str2,tmlfile,sentid1,sentid2";	$fields2 = "tid1,str1,tid1,str1,reltype,eid2,str2,tmlfile,sentid1,sentid2";	$q1 = "SELECT $fields1 from $EXPANDED_TLINKS_TABLE WHERE str2='$t' AND tid1=''";	$q2 = "SELECT $fields2 from $EXPANDED_TLINKS_TABLE WHERE str1='$t' AND tid2=''";	$rows = queryDB($q1,$q2);}elseif ($submit == 'qE-E' and $e){	$searched = 1;	$fields = "reltype,str1,str2,tmlfile,sentid1,sentid2,eid1,eid2";	$fields1 = "eid1,str1,eid1,str1,reltype,eid2,str2,tmlfile,sentid1,sentid2";	$fields2 = "eid2,str2,eid1,str1,reltype,eid2,str2,tmlfile,sentid1,sentid2";	$q1 = "SELECT $fields1 from $EXPANDED_TLINKS_TABLE WHERE str1='$e' AND tid1='' AND tid2=''";	$q2 = "SELECT $fields2 from $EXPANDED_TLINKS_TABLE WHERE str2='$e' AND tid1='' AND tid2=''";	$rows = queryDB($q1,$q2);}elseif ($submit == 'qR(E-T)'){	if ($e and $t) {		$searched = 1;		$fields1 = "reltype,str1,str2,tmlfile,sentid1,sentid2,eid1,tid2";		$fields2 = "reltype,str1,str2,tmlfile,sentid1,sentid2,tid1,eid2";		$q1 = "SELECT $fields1 from $EXPANDED_TLINKS_TABLE WHERE str1='$e' AND str2='$t' AND tid1='' AND eid2=''";		$q2 = "SELECT $fields2 from $EXPANDED_TLINKS_TABLE WHERE str1='$t' AND str2='$e' AND eid1='' AND tid2=''";		$rows = queryDB($q1,$q2);	} elseif ($e or $t) {		$warning = "WARNING: you must enter an event and a time";	}}elseif ($submit == 'qR(E-E)'){	if ($e1 and $e2) {		$searched = 1;		$fields = "reltype,str1,str2,tmlfile,sentid1,sentid2,eid1,eid2";		$q1 = "SELECT $fields from $EXPANDED_TLINKS_TABLE WHERE str1='$e1' AND str2='$e2' AND tid1='' AND tid2=''";		$q2 = "SELECT $fields from $EXPANDED_TLINKS_TABLE WHERE str1='$e2' AND str2='$e1' AND tid1='' AND tid2=''";		$rows = queryDB($q1,$q2);	} elseif ($e1 or $e2) {		$warning = "WARNING: you must enter two events";	}}function queryDB ($q1,$q2){	// Collect all rows from two database queries.	global $db_conn;	//echo "$q1<p>$q2<p>";	$result1 = mysql_query($q1, $db_conn);  	$num_rows1 = mysql_num_rows($result1);	if (mysql_errno()) { printMySqlErrors($q,$_SERVER['SCRIPT_NAME'] . ":" . __LINE__); }	$result2 = mysql_query($q2, $db_conn);  	$num_rows2 = mysql_num_rows($result2);	if (mysql_errno()) { printMySqlErrors($q2,$_SERVER['SCRIPT_NAME'] . ":" . __LINE__); }	$rows = Array();	while ($row = mysql_fetch_row($result1)) { $rows[] = $row; }	while ($row = mysql_fetch_row($result2)) { $rows[] = $row; }	return $rows;}function echoResultsQREE2 ($rows) {  	echo "<table cellpadding=7 bgcolor=$resultsColor>\n";  	foreach ($rows as $row) {		echo "\n\n\n<tr>\n<td valign=top>\n";		echoDocumentLink2($row);		echo "<td><font color=blue>".$row[0]."</font><br>";		echoTwoSentences($row[3],$row[4],$row[5],$row[6],$row[7],false);  	}  	echo "</table>\n";}function echoResultsQTE2 ($rows){	//printRows($rows);	$orderedRows = orderRows($rows);	ppRelatedEvents($orderedRows);	printOrderedRows($orderedRows);	//printListOfMatches($rows);}function orderRows ($rows){	foreach ($rows as $row)	{		$id1 = $row[2];		$id2 = $row[5];		$pivot = $row[0];		$pivotString = $row[1];				if ($pivot == $id1) {			$reltype = reverseReltype($row[4]);			$related = $id2;			$relatedString = $row[6]; }		if ($pivot == $id2) {			$reltype = $row[4];			$related = $id1;			$relatedString = $row[3]; }		if (! $relatedString) { continue; }				$adjustedRow = array($relatedString,$reltype,$pivotString,$row[7],$row[8],$row[9],$row[2],$row[5]);		if (! ( array_key_exists($reltype,$orderedRows) and				array_key_exists($relatedString,$orderedRows[$reltype]))) {			$orderedRows[$reltype][$relatedString] = array();		}		array_push($orderedRows[$reltype][$relatedString],$adjustedRow);	}	return $orderedRows;}function reverseReltype ($string){	if ($string == 'AFTER') { return 'BEFORE'; }	if ($string == 'IAFTER') { return 'IBEFORE'; }	if ($string == 'BEFORE') { return 'AFTER'; }	if ($string == 'IBEFORE') { return 'IAFTER'; }	if ($string == 'IDENTITY') { return 'IDENTITY'; }	if ($string == 'SIMULTANEOUS') { return 'SIMULTANEOUS'; }	if ($string == 'DURING') { return 'DURING'; }	if ($string == 'INCLUDES') { return 'IS_INCLUDED'; }	if ($string == 'IS_INCLUDED') { return 'INCLUDES'; }	if ($string == 'BEGINS') { return 'BEGUN_BY'; }	if ($string == 'ENDS') { return 'ENDED_BY'; }	return $string . "-R";}function printRows ($rows){  	echo "<table cellpadding=5 border=1>\n";	foreach ($rows as $row) {		$rowstring = implode('<td>',$row);		echo "<tr>\n<td valign=top>\n";		echo $rowstring; }  	echo "</table>\n";}function printListOfMatches ($rows){		echo "<table cellpadding=7>\n";	foreach ($rows as $row) {		echo "<tr>\n<td valign=top>\n";		echoDocumentLink2($row[7],$row[2],$row[5]);		$reltype = $row[4];		$str1 = $row[3];		$str2 = $row[6];		echo "<td><font color=blue><strong>[$str1]</strong> &nbsp; ";		echo "$reltype &nbsp; <strong>[$str2]</font></strong><p>\n";		echoTwoSentences($row[7],$row[8],$row[9],$row[2],$row[5],false);	}	echo "</table>\n";}function printOrderedRows ($dict){	$focus = getFocus();	echo "\n<table bgcolor=#ccffcc cellpadding=5>\n";	echo "<tr><td>\n";	echo "<table cellpadding=5 border=0><tr><td>\n";	foreach (array_keys($dict) as $reltype) {		$subdict = $dict[$reltype];		echo "<table width=100% bgcolor=#cccccc><tr><td>$reltype</table>\n";		foreach (array_keys($subdict) as $related) {			echo "\n<p><strong><a name='$reltype-$related'><font color=blue>$related $reltype $focus<br></font></a></strong></p>\n";			echo "<table cellpadding=5>";			foreach ($dict[$reltype][$related] as $row) {				echo "\n\n<tr>\n  <td valign=top>\n    ";				echoDocumentLink2($row[3],$row[6],$row[7]);				echo "\n  <td>";				echoTwoSentences($row[3],$row[4],$row[5],$row[6],$row[7],false);			}			echo "\n\n</table>\n";		}		echo "<p>\n";	}	echo "</table>\n";	echo "</table>\n";}function getFocus (){	global $e, $t;	if ($e) { return $e; }	return $t;}function ppRelatedEvents ($dict){	$focus = getFocus();	echo "\n<table bgcolor=#ccffcc cellpadding=5>\n";	echo "<tr><td>\n";	echo "\n<table cellpadding=0 border=0>\n";	echo "\n<tr>\n";	echo "  <td width=100pt>\n";	echo "  <td width=80pt>\n";	echo "  <td width=20pt>\n";	echo "  <td width=80pt>\n";	echo "  <td width=20pt>\n";	echo "  <td width=80pt>\n";	echo "  <td width=20pt>\n";	echo "  <td width=80pt>\n";	echo "  <td width=100pt>\n";	echo "\n<tr>\n";	echo "  <td colspan=2 valign=top rowspan=5 bgcolor=white>\n";	ppEventsForReltype ($dict,'BEFORE',true);	ppEventsForReltype ($dict,'IBEFORE',false);	echo "  <td>\n";	echo "  <td colspan=3 valign=top align=center bgcolor=#66dd66><strong>".strtoupper($focus)."</strong>\n";	echo "  <td>\n";	echo "  <td colspan=2 valign=top rowspan=5 bgcolor=white>\n";	ppEventsForReltype ($dict,'AFTER',true);	ppEventsForReltype ($dict,'IAFTER',false);	echo "\n<tr>\n";	echo "  <td colspan=9 height=10>\n";		echo "\n<tr>\n";	echo "  <td>\n";	echo "  <td colspan=3 valign=top bgcolor=white>\n";	ppEventsForReltype ($dict,'IDENTITY',true);	echo "  <td>\n";	echo "\n<tr>\n";	echo "  <td colspan=5 height=10>\n";	echo "\n<tr>\n";	echo "  <td colspan=1>\n";	echo "  <td colspan=3 valign=top bgcolor=white>\n";	ppEventsForReltype ($dict,'SIMULTANEOUS',true);	ppEventsForReltype ($dict,'DURING',false);	echo "  <td colspan=1>\n";	echo "\n<tr>\n";	echo "  <td colspan=9 height=10>\n";	echo "\n<tr>\n";	echo "  <td>\n";	echo "  <td colspan=3 valign=top bgcolor=white>\n";	ppEventsForReltype ($dict,'INCLUDES',true);	echo "  <td>\n";	echo "  <td colspan=3 valign=top bgcolor=white>\n";	ppEventsForReltype ($dict,'IS_INCLUDED',true);	echo "  <td>\n";	echo "\n</table>\n<p>\n";	echo "\n</table>\n<p>\n";}function ppEventsForReltype ($dict,$reltype,$printHeader){	if ($printHeader) {		global $submit;		if ($submit == 'qT-E') { $objectType = 'TIMES'; }		else { $objectType = 'EVENTS'; }		if ($reltype == 'INCLUDES') { echo "    INCLUDING $objectType<br>\n"; }		elseif ($reltype == 'IS_INCLUDED') { echo "    INCLUDED $objectType<br>\n"; }		else { echo "    $reltype<br>\n"; }	}	foreach (array_keys($dict[$reltype]) as $relatedevent) {		$size = count($dict[$reltype][$relatedevent]);		echo "    <a href='#$reltype-$relatedevent'>";		echo "$relatedevent</a> ($size)<br>\n";	}}?><!--<table bgcolor='#ccffcc' cellpadding=5><tr><td>--><?phpif ($warning) {	echo "<p><strong><font color=red>$warning</font></strong></p>\n\n";}if ($searched) {	$matchCount = count($rows);	//echo "<strong>$matchCount matches</strong><p>\n\n";	if ($submit == "qT-E") { echoResultsQTE2($rows); }	elseif ($submit == "qE-T") { echoResultsQTE2($rows); }	elseif ($submit == "qE-E") { echoResultsQTE2($rows); }	elseif ($submit == "qR(E-E)") { echoResultsQREE2($rows); }	elseif ($submit == "qR(E-T)") { echoResultsQREE2($rows); }}?></td></tr></table></body></html>