<?php require('utils.php');printVars();if ($HTTP_GET_VARS['login']) {	# use this if script was called from list.php (which uses get variables)	$login = $HTTP_GET_VARS['login'];	$file = $HTTP_GET_VARS['file'];	$source = 'previously annotated';} else {	# and this if called from home.php (using form post vars)	$file = selectNewFile($login);	$source = 'new';}$title = getDocumentTitle($file);$topic = topic($login,$file);$comment = comment($login,$file);$notrelevant = notrelevant($login,$file);?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>Event Annotator (<?php echo $file ?>)</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="timebank.css" rel="stylesheet" type="text/css"></head><body><h2>Event Annotator - Viewing events in <?php echo $source; ?> document</h2><hr><FORM action="submit.php" method="post"><input type="hidden" name="login" value="<?php echo $login; ?>" /><input type="hidden" name="file" value="<?php echo $file; ?>" /><input type="hidden" name="title" value="<?php echo $title; ?>" /><table><tr><td valign=top><table class=green cellpadding=5><tr>  <td>  Not relevant <input type=checkbox name=notrelevant <?php if ($notrelevant == 'yes') { echo 'checked'; }?>><tr>  <td>  <?php echoTopics($topic); ?><tr>  <td>  Comments:<br/>  <textarea rows=10 name=comment><?php echo $comment ?></textarea><tr>  <td>  <input type=submit value=Submit class="highlight" /></table><td><table class=yellow><tr><td><strong><font size="+1"><?php echo $title; ?></font></strong><p><?php echoDocument($file, $login); ?></table></table></FORM><hr><table><tr><td>  <form action="home.php" method="post">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Home" />  </form><td>  <form action="list.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="View list of annotated documents" />  </form><td>  <form action="index.php" method="post">  <input type="submit" value="Logout" />  </form></table></body></html>