<?php require("connection.php");require("utils.php");printVars();$login = $HTTP_GET_VARS['login'];$sorted = $HTTP_GET_VARS['sorted'];if (! $sorted) $sorted = 'no';$articles = articlesAnnotated($login);?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>Event Annotator</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="timebank.css" rel="stylesheet" type="text/css"></head><body><h2>Event Annotator - Previously annotated documents</h2><hr>[ <a href=list.php?login=<?php echo $login ?>>unsorted</a> |  <a href=list.php?login=<?php echo $login ?>&sorted=topic>sort on topic</a> ]  <p><?php if ($sorted == 'no') { 	echo "<ol>\n";	foreach ($articles as $article) 	{		$id = $article[0];		$title = $article[1];		$topic = $article[2];		$links[$topic][] = "<li><a href=article.php?file=$id&login=$login>$title</a>\n";		echo "<li><a href=article.php?file=$id&login=$login>$title [$topic] </a>\n";	}	echo "</ol>\n";} else {	foreach ($articles as $article) {		$id = $article[0];		$title = $article[1];		$topic = $article[2];		$links[$topic][] = "<li><a href=article.php?file=$id&login=$login>$title</a>\n";	}	foreach ($links as $topic => $doclist) {		if ($topic == 'none')			echo "[no topic]\n<ul>\n";		else			echo "[$topic]\n<ul>\n";		foreach ($doclist as $doc) 			echo $doc;		echo "</ul>\n";	}}?><hr><table><tr><td>   <form action="home.php" method="post">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Home" /></form><td>  <form action="article.php" method="post">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Annotate new document" /></form><td>  <form action="index.php" method="post">  <input type="submit" value="Logout" />  </form></table></body></html>