<?php require('utils.php');printVars();$login = $HTTP_POST_VARS['login'];$file = $HTTP_POST_VARS['file'];$title = $HTTP_POST_VARS['title'];$notrelevant = $HTTP_POST_VARS['notrelevant'];$topic = $HTTP_POST_VARS['topic'];$comment = $HTTP_POST_VARS['comment'];// find all events that were deemed to be relevantforeach ($HTTP_POST_VARS as $postvar => $value) {	if (preg_match('/^e\d+/', $postvar))		$relevantEvents[] = $postvar;}setDocumentJudgements($login, $file, $notrelevant, $topic, $comment);resetEventJudgements($login,$file);foreach ($relevantEvents as $event)	addJudgement($login, $file, $event, 'yes');?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>Event Annotator</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="timebank.css" rel="stylesheet" type="text/css"></head><body><h2>Event Annotator - Submitting Results</h2><hr>Judgements have been saved for <blockquote><a href=article.php?login=<?php echo $login ?>&file=<?php echo $file ?>><?php echo $title ?></a></blockquote><hr><table><tr><td>  <form action="home.php" method="post">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Home" />  </form><td>  <form action="list.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="View list of annotated documents" />  </form><td>  <form action="article.php" method="post">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Annotate new document" />  </form><td>  <form action="index.php" method="post">  <input type="submit" value="Logout" />  </form></table></body></html>