<?php require('../utils.php');#printVars();$login = $HTTP_GET_VARS['login'];$file = $HTTP_GET_VARS['file'];$query = "SELECT * FROM $DOCS_TABLE WHERE file='$file';";$rows = dbSelect($query, 'getSentences');if ($rows[0][2] == $login) {	$useTimeBank = true; } else {	$useTimeBank = false; }	function getSentences($login,$file) {	global $SENTENCES_TABLE;	$query = "SELECT * FROM $SENTENCES_TABLE WHERE file='$file';";	$rows = dbSelect($query, 'getSentences');	return $rows;}function getAnnotations($login, $file) {	global $RELTYPES_TABLE;	$query = "SELECT * FROM $RELTYPES_TABLE WHERE file='$file';";	$rows = dbSelect($query, 'getAnnotations');	$annotations = Array();	foreach ($rows as $row) {		$lid = $row[1];		$ann = $row[2];		$rel = $row[3];		$annotations[$lid][$ann] = $rel;	}	return $annotations;}function getRelType($login, $lid, $annotations) {	global $useTimeBank;	if ($annotations[$lid][$login]) {		return $annotations[$lid][$login];	} elseif ($useTimeBank) {		return $annotations[$lid]['tb'];	} else {		return 'UNKNOWN';	}}?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>TempEval Document <?php echo $file ?> (<?php echo $login ?>)</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="tempeval.css" rel="stylesheet" type="text/css"></head><body><h2>TempEval Document <?php echo $file ?></h2><hr><FORM action="submit.php" method="post"><input type="hidden" name="login" value="<?php echo $login; ?>" /><input type="hidden" name="file" value="<?php echo $file; ?>" /><?php	$sentences = getSentences($login,$file);	$annotations = getAnnotations($login,$file);#	echo "<pre>\n";#	print_r($sentences);#	print_r($annotations);#	echo "</pre>\n";	echo "<table cellpadding=3 border=0>\n";	$count = 0;  	foreach ($sentences as $sentence) 	{		$count = $count + 1;		$id = $sentence[1];		$lid = $sentence[2];		$string = $sentence[3];		$rel = getRelType($login, $lid, $annotations);				$checkedB = '';		$checkedA = '';		$checkedO = '';		$checkedV = '';		$checkedBO = '';		$checkedOA = '';		$checkedBA = '';		$checkedNR = '';		if ($rel == 'BEFORE') { $checkedB = ' checked'; }		if ($rel == 'AFTER') { $checkedA = ' checked'; }		if ($rel == 'OVERLAP') { $checkedO = ' checked'; }		if ($rel == 'VAGUE') { $checkedV = ' checked'; }		if ($rel == 'BEFORE-OR-OVERLAP') { $checkedBO = ' checked'; }		if ($rel == 'OVERLAP-OR-AFTER') { $checkedOA = ' checked'; }		if ($rel == 'BEFORE-OR-AFTER') { $checkedBA = ' checked'; }		if ($rel == 'NON-RELATED') { $checkedNR = ' checked'; }				echo "<tr>\n";		echo "  <td valign=top>$id\n";		echo "  <td valign=top>";		if ($id == 0) {			echo "<TARGETTIMEX3>$string</TARGETTIMEX3>\n";		} else {			echo "$string\n";		}		echo "  <td valign=top width=140>\n";		if (strpos($string,'</TARGET')) {			echo "    <table border=0 cellpadding=3>\n";   		 	echo "      <tr>\n";			echo "        <td><input type=radio name=$lid value=B$checkedB>B</td>\n";			echo "        <td><input type=radio name=$lid value=O$checkedO>O</td>\n";			echo "        <td><input type=radio name=$lid value=A$checkedA>A</td>\n";			echo "    </table>\n";			echo "    <table border=0 cellpadding=3>\n";   		 	echo "      <tr>\n";			echo "        <td><input type=radio name=$lid value=V$checkedV>V</td>\n";			echo "        <td><input type=radio name=$lid value=BO$checkedBO>BO</td>\n";			echo "        <td><input type=radio name=$lid value=OA$checkedOA>OA</td>\n";			echo "      </tr>\n";			echo "    </table>\n";		} else {			echo "&nbsp\n";		}	}	echo "</table>\n";?><p><input type="submit" value="Submit Judgements" /></p></FORM><hr><table><tr><td>  <form action="index.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Home" />  </form><td>  <form action="articles.php" method="get">  <input type="hidden" name="login" value="<?php echo 