<?php require('utils.php');$debug = false;if ($debug) { printVars(); }$login = $HTTP_POST_VARS['login'];$file = $HTTP_POST_VARS['file'];// find all links that were changedforeach ($HTTP_POST_VARS as $postvar => $value) {	if (preg_match('/^l\d+/', $postvar))		$lids[] = $postvar;}foreach ($lids as $lid) {	$rel = $HTTP_POST_VARS[$lid];	$rel = expandRel($rel);	$query = "SELECT COUNT(*) FROM $ADJUCATED_RELTYPES_TABLE WHERE file='$file' AND annotator='$login' AND linkID='$lid';";	$rows = dbSelect($query, 'submit.php');	$row = $rows[0];	$count = $row[0];	if ($count > 0) {		$query = "UPDATE $ADJUCATED_RELTYPES_TABLE " . 			 "SET reltype='$rel' WHERE file='$file' AND linkID='$lid' AND annotator='$login';";		if ($debug) { echo $query . '<br>'; }		dbUpdate($query,'setDocumentJudgements()');	} else {		$query = "INSERT INTO $ADJUCATED_RELTYPES_TABLE " .			 "VALUES ('$file', '$lid', '$login', '$rel');";		if ($debug) { echo $query . '<br>'; }		dbInsert($query, 'setDocumentJudgements');	}}function expandRel($rel) {	if ($rel == 'B') { return 'BEFORE'; }	if ($rel == 'A') { return 'AFTER'; }	if ($rel == 'O') { return 'OVERLAP'; }	if ($rel == 'V') { return 'VAGUE'; }	if ($rel == 'BO') { return 'BEFORE-OR-OVERLAP'; }	if ($rel == 'OA') { return 'OVERLAP-OR-AFTER'; }	if ($rel == 'BA') { return 'BEFORE-OR-AFTER'; }	if ($rel == 'NR') { return 'NON-RELATED'; }	return $rel;}?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>TempEval Links Submission</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="../../timebank.css" rel="stylesheet" type="text/css"></head><body><h2>Submitted Results For <?php echo $file ?></h2><hr>Judgements have been saved for:<blockquote><a href=adminArticle.php?login=<?php echo $login ?>&file=<?php echo $file ?>><?php echo $file ?></a></blockquote><hr><table><tr><td>  <form action="adminLogin.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Home" />  </form><td>  <form action="adminArticles.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Document List" />  </form></table></body></html>