<?php require('utils.php');$file = $HTTP_GET_VARS['file'];function getArticles($login){	global $SENTENCES_TABLE;	global $DOCS_TABLE;	$query = "SELECT DISTINCT file FROM $SENTENCES_TABLE;";	$query = "SELECT file FROM $DOCS_TABLE WHERE annotator1='$login' OR annotator2='$login';";	$rows = dbSelect($query, 'getArticles');	$files = array();	foreach ($rows as $row) {		$file = $row[0];		$files[] = $file;	}	return $files;}function getCounts($login){	global $RELTYPES_TABLE;	global $SENTENCES_TABLE;	global $MAINEVENTS_TABLE;	global $MAINEVENT_COUNTS_TABLE;	$query1 = "SELECT file, sentCount FROM $MAINEVENT_COUNTS_TABLE;";	$fullCounts = dbSelect($query1, 'getCounts1');	$query2 = "SELECT file, count(*) FROM $MAINEVENTS_TABLE where annotator='$login' group by file;";	$annotatorCounts = dbSelect($query2, 'getCounts2');	#echo $query1 . '<br>';	$counts = Array();	foreach ($fullCounts as $fullCount) {		$counts[$fullCount[0]]['full'] = $fullCount[1];		#print $fullCount[0] . ':' . $fullCount[1] . '<br>';	}	foreach ($annotatorCounts as $fullCount) {		$counts[$fullCount[0]]['done'] = $fullCount[1];		#print $fullCount[0] . ':' . $fullCount[1] . '<br>';	}	return $counts;}?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>TempEval Documents</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="../timebank.css" rel="stylesheet" type="text/css"></head><body><h2>TempEval documents assigned to "<?php echo $login; ?>"</h2><hr><?php	$files = getArticles($login);	$counts = getCounts($login);	echo "<blockquote>\n";	echo "<table cellpadding=5>\n";	$count = 0;  	foreach ($files as $file) {		$count++;		$allLinks = $counts[$file]['full'];		$doneLinks = $counts[$file]['done'];		if (!$doneLinks) {			$doneLinks = 0;		}		$check = '';		if ($allLinks == $doneLinks) {			$check = 'done';		}		echo "<tr>\n";		echo "  <td align=right>$count\n";		echo "  <td><a href=article.php?login=$login&file=$file>$file</a>\n";		echo "  <td align=right>$allLinks\n";		echo "  <td align=right>$doneLinks\n";		echo "  <td>$check\n";	}	echo "</table>\n";	echo "</blockquote>\n";?><hr><table><tr><td>  <form action="index.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Home" />  </form><td>  <form action="articles.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Document List" />  </form></table></body></html>