<?php require('utils.php');$debug = false;if ($debug) { printVars(); }$login = $HTTP_POST_VARS['login'];$file = $HTTP_POST_VARS['file'];// find all submitted events$eids = array();foreach ($HTTP_POST_VARS as $postvar => $value) {	if (preg_match('/^e\d+/', $value))		$eids[] = $value;}$query = "DELETE FROM $MAINEVENTS_TABLE WHERE file='$file' AND annotator='$login';";if ($debug) { echo "$query<br>"; }dbDelete($query, "submit.php: deleting main events");foreach ($eids as $eid) {	$query = "INSERT INTO $MAINEVENTS_TABLE " . "VALUES ('$file', 0, '$eid', '$login');";	if ($debug) { echo "$query<br>"; }	dbInsert($query, 'submit.php: adding main event');}?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>TempEval Links Submission</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="../timebank.css" rel="stylesheet" type="text/css"></head><body><h2>Submitted Results For <?php echo $file ?></h2><hr>Judgements have been saved for:<blockquote><a href=article.php?login=<?php echo $login ?>&file=<?php echo $file ?>><?php echo $file ?></a></blockquote><hr><table><tr><td>  <form action="index.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Home" />  </form><td>  <form action="articles.php" method="get">
