ing) echo $query."<br>\n";	$result = mysql_query($query, $db_conn);	if (mysql_errno()) {    		echo '<p><hr><p>MySQL Error:' . mysql_errno() . ": " . mysql_error(). "\n";		echo "<p>Query: $query<p>Caller: $caller<p><hr><p>\n\n"; 	}	return $result;}?>