<?php require('utils.php');$debug = false;#$debug = true;if ($debug) { printVars(); }$file = $HTTP_POST_VARS['file'];// find all links that were changed$assignments = array();foreach ($HTTP_POST_VARS as $postvar => $value) {	#echo "$postvar $value<br>";	if (preg_match('/^\(e\d+-e\d+\)/', $postvar)) {		$assignments[$postvar] = $value; }}if ($debug) {	echo "ASSIGNMENTS: ";	print_r($assignments);}if ($debug) print "<p>";foreach ($assignments as $events => $rel) {	$rel = expandRel($rel);	$query = "SELECT COUNT(*) FROM $ADJUDICATED_MAINEVENT_RELTYPES_TABLE " . 		 "WHERE file='$file' AND events='$events';";	$rows = dbSelect($query, 'adminSubmit.php');	$row = $rows[0];	$count = $row[0];	if ($count > 0) {		$query = "UPDATE $ADJUDICATED_MAINEVENT_RELTYPES_TABLE " . 			 "SET reltype='$rel' " .			 "WHERE file='$file' AND events='$events';";		if ($debug) { echo $query . '<br>'; }		dbUpdate($query,'adminSubmit()');	} else {		$query = "INSERT INTO $ADJUDICATED_MAINEVENT_RELTYPES_TABLE " .			 "VALUES ('$file', '', '$events', '$rel');";		if ($debug) { echo $query . '<br>'; }		dbInsert($query, 'adminSubmit');	}}function expandRel($rel) {	if ($rel == 'B') { return 'BEFORE'; }	if ($rel == 'A') { return 'AFTER'; }	if ($rel == 'O') { return 'OVERLAP'; }	if ($rel == 'V') { return 'VAGUE'; }	if ($rel == 'BO') { return 'BEFORE-OR-OVERLAP'; }	if ($rel == 'OA') { return 'OVERLAP-OR-AFTER'; }	if ($rel == 'BA') { return 'BEFORE-OR-AFTER'; }	if ($rel == 'NR') { return 'NON-RELATED'; }	return $rel;}?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>TempEval Links Submission</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="../../timebank.css" rel="stylesheet" type="text/css"></head><body><h2>Submitted Results For <?php echo $file ?></h2><hr>Judgements have been saved for:<blockquote><a href=adminArticle.php?login=<?php echo $login ?>&file=<?php echo $file ?>>