<?php require('utils.php');$debug = false;if ($debug) { printVars(); }$login = $HTTP_POST_VARS['login'];$file = $HTTP_POST_VARS['file'];function expandRel($rel) {	if ($rel == 'B') { return 'BEFORE'; }	if ($rel == 'A') { return 'AFTER'; }	if ($rel == 'O') { return 'OVERLAP'; }	if ($rel == 'V') { return 'VAGUE'; }	if ($rel == 'BO') { return 'BEFORE-OR-OVERLAP'; }	if ($rel == 'OA') { return 'OVERLAP-OR-AFTER'; }	if ($rel == 'BA') { return 'BEFORE-OR-AFTER'; }	if ($rel == 'NR') { return 'NON-RELATED'; }	return $rel;}// find all submitted events$reltypes = array();foreach ($HTTP_POST_VARS as $postvar => $value) {	if (preg_match('/^\(e\d+-e\d+\)/', $postvar)) {		$reltypes[$postvar] = expandRel($value);	}}#if ($debug) { print_r($reltypes); }$query = "DELETE FROM $MAINEVENT_RELTYPES_TABLE WHERE file='$file' AND annotator='$login';";if ($debug) { echo "$query<br>"; }dbDelete($query, "submit.php: deleting main events");foreach ($reltypes as $events => $reltype) {	$query = "INSERT INTO $MAINEVENT_RELTYPES_TABLE " . "VALUES ('$file', '$login', '$events', '$reltype');";	if ($debug) { echo "$query<br>"; }	dbInsert($query, 'submit.php: adding main event');}?><!doctype html public "-//W3C//DTD HTML 4.0 //EN"> <html><head><title>TempEval Links Submission</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><link href="../timebank.css" rel="stylesheet" type="text/css"></head><body><h2>Submitted Results For <?php echo $file ?></h2><hr>Judgements have been saved for:<blockquote><a href=article.php?login=<?php echo $login ?>&file=<?php echo $file ?>><?php echo $file ?></a></blockquote><hr><table><tr><td>  <form action="index.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Home" />  </form><td>  <form action="articles.php" method="get">  <input type="hidden" name="login" value="<?php echo $login; ?>" />  <input type="submit" value="Document List" />  </form></table></body></html>